import React from 'react'

export const TodoItem = ({ todo, onDelete }) => {

  if (!todo) {
    return null; // safety check
  }

  return (
    <>
      <div>
        <h4>{todo.title}</h4>
        <p>{todo.desc}</p>

        <button
          className="btn btn-sm btn-danger"
          onClick={() => onDelete && onDelete(todo)}
        >
          Delete
        </button>
      </div>
      <hr />
    </>
  )
}


export default Todo
