import React from 'react';
import { TodoItem } from "./TodoItem";

export const Todos = ({ todos, onDelete }) => {
  return (
    <>
      <h3 className="mb-3">Todos List</h3>

      {todos.length === 0 ? (
        <p className="empty">No todos to display</p>
      ) : (
        todos.map((todo) => (
          <TodoItem
            todo={todo}
            key={todo.sno}
            onDelete={onDelete}
          />
        ))
      )}
    </>
  );
};
